using J=System.Console;using System.Linq;class P{static void Main(){var c=C().Select(x=>int.Parse(x)).ToArray();var l=new System.Collections.Generic.Dictionary<int,int>();for(int i=0;i<c[7];i++){var e=C().Select(x=>int.Parse(x)).ToArray();l.Add(e[0], e[1]);}l.Add(c[3],c[4]);while(true){var q=C();var r=q.Take(2).Select(x=>int.Parse(x)).ToArray();J.WriteLine((r[0]==-1||r[1]==l[r[0]]||(q.Last()!="LEFT")^(r[1]>l[r[0]]))?"WAIT":"BLOCK");}}static string[]C(){return J.ReadLine().Split(' ');}}